mapper.select:
SELECT BOARDSEQ, ID, TITLE, CONTENT, WDATE, DEL, READCOUNT
FROM BOARDALL
WHERE BOARDSEQ=#{ boardseq }

mapper.update:
UPDATE BOARDALL
SET READCOUNT = READCOUNT + 1
WHERE BOARDSEQ=#{ boardseq }

UPDATE BOARDALL
SET TITLE = #{title}, CONTENT=#{content}
WHERE BOARDSEQ=#{ boardseq }

UPDATE BOARDALL
SET DEL=1
WHERE BOARDSEQ=#{ boardseq }
